@tailwind base;
@tailwind components;
@tailwind utilities;

/* Ultra Professional Premium Dark Theme */
:root {
  --bg-primary: #0a0a0b;
  --bg-secondary: #111111;
  --bg-card: rgba(30, 30, 35, 0.4);
  --bg-card-hover: rgba(30, 30, 35, 0.6);
  --border-glow: rgba(139, 92, 246, 0.3);
  --border-glow-hover: rgba(139, 92, 246, 0.5);
  --text-primary: #f8fafc;
  --text-secondary: #94a3b8;
  --text-muted: #64748b;
  --glow-purple: rgba(139, 92, 246, 0.5);
  --glow-cyan: rgba(34, 211, 238, 0.5);
  --glow-emerald: rgba(52, 211, 153, 0.5);
  --glow-orange: rgba(251, 146, 60, 0.5);
}

/* Professional Background */
.pro-bg {
  background: 
    radial-gradient(circle at 20% 50%, rgba(120, 119, 198, 0.05) 0%, transparent 50%),
    radial-gradient(circle at 80% 20%, rgba(255, 119, 198, 0.05) 0%, transparent 50%),
    radial-gradient(circle at 40% 80%, rgba(139, 92, 246, 0.05) 0%, transparent 50%),
    linear-gradient(135deg, #0a0a0b 0%, #111111 100%);
  min-height: 100vh;
}

/* Premium Card System */
.premium-card {
  @apply bg-slate-800/40 border border-slate-600/50 rounded-lg backdrop-blur-sm;
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  box-shadow: 
    0 4px 6px -1px rgba(0, 0, 0, 0.1),
    0 2px 4px -1px rgba(0, 0, 0, 0.06),
    inset 0 1px 0 0 rgba(255, 255, 255, 0.05);
}

.premium-card:hover {
  @apply border-slate-500/70;
  box-shadow: 
    0 10px 15px -3px rgba(139, 92, 246, 0.1),
    0 4px 6px -2px rgba(139, 92, 246, 0.05),
    inset 0 1px 0 0 rgba(255, 255, 255, 0.1);
