}

/* Color-specific Card Variants */
.card-purple {
  @apply border-purple-500/30;
}
.card-purple:hover {
  @apply border-purple-400/50;
  box-shadow: 
    0 10px 15px -3px rgba(139, 92, 246, 0.15),
    0 4px 6px -2px rgba(139, 92, 246, 0.1);
}

.card-cyan {
  @apply border-cyan-500/30;
}
.card-cyan:hover {
  @apply border-cyan-400/50;
  box-shadow: 
    0 10px 15px -3px rgba(34, 211, 238, 0.15),
    0 4px 6px -2px rgba(34, 211, 238, 0.1);
}

.card-emerald {
  @apply border-emerald-500/30;
}
.card-emerald:hover {
  @apply border-emerald-400/50;
  box-shadow: 
    0 10px 15px -3px rgba(52, 211, 153, 0.15),
    0 4px 6px -2px rgba(52, 211, 153, 0.1);
}

.card-blue {
  @apply border-blue-500/30;
}
.card-blue:hover {
  @apply border-blue-400/50;
  box-shadow: 
    0 10px 15px -3px rgba(59, 130, 246, 0.15),
    0 4px 6px -2px rgba(59, 130, 246, 0.1);
}

.card-orange {
  @apply border-orange-500/30;
}
.card-orange:hover {
  @apply border-orange-400/50;
  box-shadow: 
    0 10px 15px -3px rgba(251, 146, 60, 0.15),
    0 4px 6px -2px rgba(251, 146, 60, 0.1);
}

.card-red {
  @apply border-red-500/30;
}
.card-red:hover {
  @apply border-red-400/50;
  box-shadow: 
    0 10px 15px -3px rgba(239, 68, 68, 0.15),
    0 4px 6px -2px rgba(239, 68, 68, 0.1);
}

/* Text Glow Effects */
.text-glow-purple {
  color: #a855f7;
  text-shadow: 0 0 10px rgba(168, 85, 247, 0.5);
}

.text-glow-cyan {
  color: #22d3ee;
  text-shadow: 0 0 10px rgba(34, 211, 238, 0.5);
}

.text-glow-emerald {
  color: #34d399;
  text-shadow: 0 0 10px rgba(52, 211, 153, 0.5);
}

.text-glow-orange {
  color: #fb923c;
  text-shadow: 0 0 10px rgba(251, 146, 60, 0.5);
}

/* Premium Button System */
.btn-premium {
  @apply px-3 py-1.5 text-xs font-medium rounded-md transition-all duration-300;
  box-shadow: 
    0 1px 3px 0 rgba(0, 0, 0, 0.1),
    0 1px 2px 0 rgba(0, 0, 0, 0.06);
}

.btn-premium:hover {
  transform: translateY(-1px);
  box-shadow: 
    0 4px 6px -1px rgba(0, 0, 0, 0.1),
    0 2px 4px -1px rgba(0, 0, 0, 0.06);
}

/* Animation Classes */
@keyframes pulse-glow {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

.animate-pulse-glow {
  animation: pulse-glow 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

/* Premium Spacing System */
.spacing-compact {
  @apply space-y-2;
}

.spacing-premium {
  @apply space-y-4;
}

.spacing-luxury {
  @apply space-y-6;
}

/* Responsive Design Utilities */
@media (max-width: 640px) {
  /* Mobile optimizations */
  .premium-card {
    @apply p-3;
  }

  .text-glow {
    text-shadow: 0 0 5px rgba(34, 211, 238, 0.3);
  }

  .text-glow-hover:hover {
    text-shadow: 0 0 8px rgba(34, 211, 238, 0.4);
  }

  /* Reduce excessive padding on mobile */
  .mobile-compact {
    @apply px-2 py-2;
  }

  /* Better button sizing for mobile */
  .btn-mobile {
    @apply text-sm px-3 py-2;
  }
}

@media (min-width: 641px) and (max-width: 1024px) {
  /* Tablet optimizations */
  .premium-card {
    @apply p-4;
  }

  /* Balance text sizes for tablets */
  .text-responsive {
    @apply text-base;
  }
}

@media (min-width: 1025px) {
  /* Desktop optimizations */
  .premium-card {
    @apply p-6;
  }

  /* Enhanced effects for desktop */
  .text-glow {
    text-shadow: 0 0 15px rgba(34, 211, 238, 0.5);
  }

  .text-glow-hover:hover {
    text-shadow: 0 0 20px rgba(34, 211, 238, 0.7);
  }
}

