}

.dark {
  --background: hsl(240, 12%, 5%);
  --background-secondary: hsl(260, 15%, 8%);
  --background-tertiary: hsl(270, 12%, 12%);
  --foreground: hsl(280, 20%, 95%);
  --foreground-secondary: hsl(270, 15%, 85%);
  --muted: hsl(260, 15%, 15%);
  --muted-foreground: hsl(270, 12%, 65%);
  --popover: hsl(240, 12%, 5%);
  --popover-foreground: hsl(280, 20%, 95%);
  --card: hsl(260, 15%, 8%);
  --card-foreground: hsl(280, 20%, 95%);
  --border: hsl(260, 15%, 20%);
  --input: hsl(260, 15%, 12%);
  --primary: hsl(270, 80%, 65%);
  --primary-foreground: hsl(280, 20%, 95%);
  --secondary: hsl(260, 15%, 15%);
  --secondary-foreground: hsl(270, 15%, 85%);
  --accent: hsl(270, 60%, 55%);
  --accent-foreground: hsl(280, 20%, 95%);
  --destructive: hsl(0, 70%, 55%);
  --destructive-foreground: hsl(280, 20%, 95%);
  --ring: hsl(270, 80%, 65%);
}

@layer base {
  * {
    @apply border-slate-600;
  }

  body {
    @apply font-sans antialiased;
    font-family: 'Inter', 'SF Pro Display', -apple-system, sans-serif;
    background: 
      radial-gradient(ellipse at top, hsl(270, 50%, 15%) 0%, transparent 50%),
      radial-gradient(ellipse at bottom, hsl(240, 40%, 10%) 0%, transparent 50%),
      linear-gradient(135deg, 
        hsl(240, 15%, 3%) 0%, 
        hsl(260, 20%, 6%) 25%,
        hsl(270, 25%, 4%) 50%,
        hsl(280, 20%, 6%) 75%,
        hsl(240, 15%, 3%) 100%
      );
    color: hsl(280, 20%, 95%);
    min-height: 100vh;
    position: relative;
    overflow-x: hidden;
  }

  body::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: 
      radial-gradient(circle at 20% 80%, hsla(270, 100%, 70%, 0.15) 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, hsla(290, 100%, 65%, 0.1) 0%, transparent 50%),
      radial-gradient(circle at 40% 40%, hsla(260, 100%, 75%, 0.08) 0%, transparent 50%);
    pointer-events: none;
    z-index: -1;
    animation: ambient-flow 20s ease-in-out infinite;
  }

  /* Premium spacing utilities */
  .space-y-premium > * + * {
    margin-top: 2rem;
  }

  .space-y-premium-lg > * + * {
    margin-top: 3rem;
  }

  /* XORA-style 3D Transform utilities */
  .transform-3d {
    transform-style: preserve-3d;
  }

  .perspective-1000 {
    perspective: 1000px;
  }

  /* Enhanced animations for XORA style */
  @keyframes float-3d {
    0%, 100% { 
      transform: translateY(0px) rotateX(0deg) rotateY(0deg); 
    }
    50% { 
      transform: translateY(-20px) rotateX(5deg) rotateY(5deg); 
    }
  }

  .animate-float-3d {
    animation: float-3d 6s ease-in-out infinite;
  }

  @keyframes glow-pulse {
    0%, 100% { 
      box-shadow: 0 0 20px rgba(139, 92, 246, 0.4);
    }
    50% { 
      box-shadow: 0 0 40px rgba(139, 92, 246, 0.8);
    }
  }

  .animate-glow-pulse {
    animation: glow-pulse 3s ease-in-out infinite;
  }

  /* Enhanced Text Glow Effects - Professional */
  .text-glow {
    text-shadow: 0 0 10px rgba(139, 92, 246, 0.4), 0 0 20px rgba(139, 92, 246, 0.3), 0 0 30px rgba(139, 92, 246, 0.1);
  }

  .text-glow-cyan {
    text-shadow: 0 0 10px rgba(6, 182, 212, 0.5), 0 0 20px rgba(6, 182, 212, 0.3), 0 0 30px rgba(6, 182, 212, 0.1);
  }

  .text-glow-white {
    text-shadow: 0 0 8px rgba(255, 255, 255, 0.3), 0 0 16px rgba(255, 255, 255, 0.2), 0 0 24px rgba(255, 255, 255, 0.1);
  }

  .text-glow-hover {
    transition: text-shadow 0.3s ease;
  }

  .text-glow-hover:hover {
    text-shadow: 0 0 15px rgba(139, 92, 246, 0.6), 0 0 30px rgba(139, 92, 246, 0.4), 0 0 45px rgba(139, 92, 246, 0.2);
  }

  /* Professional Card Text Glow */
  .card-text-glow {
    text-shadow: 0 0 8px rgba(255, 255, 255, 0.2), 0 0 16px rgba(6, 182, 212, 0.1);
  }

  .card-title-glow {
    text-shadow: 0 0 10px rgba(255, 255, 255, 0.3), 0 0 20px rgba(139, 92, 246, 0.2);
  }

  /* 4D Icon Effects */
  @keyframes icon-float {
    0%, 100% { transform: translateY(0px) rotateX(0deg); }
    50% { transform: translateY(-4px) rotateX(5deg); }
  }

  @keyframes icon-glow-pulse {
    0%, 100% { 
      box-shadow: 0 0 20px rgba(139, 92, 246, 0.3), 0 0 40px rgba(6, 182, 212, 0.2), inset 0 0 20px rgba(139, 92, 246, 0.1);
    }
    50% { 
      box-shadow: 0 0 30px rgba(139, 92, 246, 0.5), 0 0 60px rgba(6, 182, 212, 0.3), inset 0 0 30px rgba(139, 92, 246, 0.2);
    }
  }

  .icon-4d {
    perspective: 1000px;
    transform-style: preserve-3d;
    animation: icon-float 3s ease-in-out infinite;
  }

  .icon-4d-glow {
    animation: icon-glow-pulse 2s ease-in-out infinite;
  }

  .icon-4d:hover {
    animation-play-state: paused;
    transform: translateY(-6px) rotateX(10deg) rotateY(5deg) scale(1.1);
  }

  /* Premium Card Glow Border */
  @keyframes premium-border-glow {
    0%, 100% { 
      box-shadow: 0 0 20px rgba(139, 92, 246, 0.2), 0 0 40px rgba(6, 182, 212, 0.1), inset 0 0 20px rgba(139, 92, 246, 0.05);
    }
    50% { 
      box-shadow: 0 0 30px rgba(139, 92, 246, 0.4), 0 0 60px rgba(6, 182, 212, 0.2), inset 0 0 30px rgba(139, 92, 246, 0.1);
    }
  }

  .premium-card-glow {
    animation: premium-border-glow 4s ease-in-out infinite;
  }

  /* Border Glow Effects */
  .border-glow {
    box-shadow: 0 0 15px rgba(139, 92, 246, 0.2), inset 0 0 15px rgba(139, 92, 246, 0.1);
  }

  .border-glow-cyan {
    box-shadow: 0 0 15px rgba(6, 182, 212, 0.3), inset 0 0 15px rgba(6, 182, 212, 0.1);
  }

  /* Subtle Pulse Animation for Phone Frame */
  @keyframes phone-pulse {
    0%, 100% { 
      box-shadow: 0 0 20px rgba(139, 92, 246, 0.3);
    }
    50% { 
      box-shadow: 0 0 40px rgba(139, 92, 246, 0.5);
    }
  }

  .animate-phone-pulse {
    animation: phone-pulse 4s ease-in-out infinite;
  }

  /* Enhanced Gradient Border Animation */
  @keyframes gradient-border {
    0%, 100% { 
      background-position: 0% 50%;
    }
    50% { 
      background-position: 100% 50%;
    }
  }

  .gradient-border {
    background: linear-gradient(45deg, #06b6d4, #8b5cf6, #ec4899, #06b6d4);
    background-size: 300% 300%;
    animation: gradient-border 4s ease infinite;
  }

  /* Premium Section Glow */
  .premium-section-glow {
    box-shadow: 
      0 0 40px rgba(6, 182, 212, 0.15),
      0 0 80px rgba(139, 92, 246, 0.1),
      0 0 120px rgba(236, 72, 153, 0.05),
      inset 0 2px 0 rgba(255, 255, 255, 0.1);
  }

  /* Premium Text Glow */
  .text-glow-premium {
    text-shadow: 
      0 0 10px rgba(6, 182, 212, 0.3),
      0 0 20px rgba(139, 92, 246, 0.2),
      0 0 30px rgba(236, 72, 153, 0.1);
  }

  /* 4D Icon Premium */
  .icon-4d-premium {
    transform-style: preserve-3d;
    perspective: 1000px;
    animation: icon-float-premium 3s ease-in-out infinite;
  }

  .icon-4d-premium:hover {
    animation-play-state: paused;
    transform: perspective(1000px) rotateX(15deg) rotateY(15deg) scale(1.1);
  }

  @keyframes icon-float-premium {
    0%, 100% { 
      transform: perspective(1000px) rotateX(0deg) rotateY(0deg) translateY(0px);
    }
    50% { 
      transform: perspective(1000px) rotateX(10deg) rotateY(10deg) translateY(-4px);
    }
  }

  /* Animated Border Gradient */
  .border-gradient-animated {
    background: linear-gradient(45deg, 
      rgba(6, 182, 212, 0.4),
      rgba(139, 92, 246, 0.4),
      rgba(236, 72, 153, 0.4),
      rgba(6, 182, 212, 0.4)
    );
    background-size: 400% 400%;
    animation: gradient-shift 4s ease-in-out infinite;
  }

  @keyframes gradient-shift {
    0%, 100% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
  }

  /* Premium Shine Effect */
  @keyframes shine {
    0% { transform: translateX(-100%); }
    100% { transform: translateX(100%); }
  }

  .shine-effect {
    position: relative;
    overflow: hidden;
  }

  .shine-effect::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    transition: left 0.5s;
  }

  .shine-effect:hover::before {
    left: 100%;
  }

  /* Responsive Mobile Optimizations */
  @media (max-width: 768px) {
    .mobile-compact {
      padding: 1rem;
    }

    .mobile-text-sm {
      font-size: 0.875rem;
    }

    .mobile-spacing {
      margin-bottom: 1.5rem;
    }
  }

  .space-y-premium-xl > * + * {
    margin-top: 4rem;
  }
}

@layer components {
  .glow-text {
    text-shadow: 
      0 0 5px var(--neon-purple), 
      0 0 10px var(--neon-purple), 
      0 0 20px var(--neon-purple),
      0 0 40px var(--neon-purple);
    font-weight: 600;
    letter-spacing: 0.02em;
  }

  .glow-text-cyan {
    text-shadow: 
      0 0 5px var(--neon-cyan), 
      0 0 10px var(--neon-cyan), 
      0 0 20px var(--neon-cyan);
    color: var(--neon-cyan);
  }

  .glow-border {
    box-shadow: 
      0 0 15px hsla(270, 100%, 70%, 0.4), 
      inset 0 0 15px hsla(270, 100%, 70%, 0.1),
      0 0 30px hsla(270, 100%, 70%, 0.2);
    border: 1px solid hsla(270, 100%, 70%, 0.4);
  }

  .glass-card {
    background: linear-gradient(135deg, 
      hsla(260, 15%, 8%, 0.9) 0%, 
      hsla(270, 12%, 12%, 0.8) 50%, 
      hsla(280, 10%, 10%, 0.9) 100%);
    backdrop-filter: blur(20px);
    border: 1px solid hsla(270, 30%, 30%, 0.3);
    box-shadow: 
      0 8px 32px hsla(270, 50%, 10%, 0.4),
      inset 0 1px 0 hsla(280, 20%, 95%, 0.1);
  }

  .premium-card {
    background: linear-gradient(135deg, 
      hsla(260, 15%, 8%, 0.95) 0%, 
      hsla(270, 12%, 10%, 0.9) 100%);
    backdrop-filter: blur(25px);
    border: 1px solid hsla(270, 40%, 40%, 0.2);
    box-shadow: 
      0 20px 40px hsla(240, 50%, 5%, 0.6),
      0 0 0 1px hsla(270, 30%, 30%, 0.1),
      inset 0 1px 0 hsla(280, 20%, 95%, 0.05);
  }

  .neon-button {
    position: relative;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    background: linear-gradient(135deg, 
      hsla(270, 80%, 65%, 0.8) 0%, 
      hsla(280, 70%, 60%, 0.9) 100%);
    border: 1px solid hsla(270, 80%, 65%, 0.6);
    font-weight: 600;
    letter-spacing: 0.025em;
  }

  .neon-button:hover {
    transform: translateY(-3px);
    box-shadow: 
      0 0 25px hsl(270, 100%, 70%), 
      0 0 50px hsl(270, 100%, 70%), 
      0 0 75px hsl(270, 100%, 70%),
      0 10px 30px hsla(270, 50%, 30%, 0.4);
    background: linear-gradient(135deg, 
      hsla(270, 80%, 70%, 0.9) 0%, 
      hsla(280, 70%, 65%, 1) 100%);
  }

  .neon-button:active {
    transform: translateY(-1px);
  }

  .navbar-blur {
    backdrop-filter: blur(25px);
    background: linear-gradient(135deg, 
      hsla(240, 12%, 5%, 0.95) 0%, 
      hsla(260, 15%, 8%, 0.9) 100%);
    border-bottom: 1px solid hsla(270, 30%, 30%, 0.2);
  }

  /* Premium form inputs */
  .premium-input {
    background: linear-gradient(135deg, 
      hsla(260, 15%, 12%, 0.9) 0%, 
      hsla(270, 12%, 10%, 0.95) 100%);
    border: 1px solid hsla(270, 30%, 25%, 0.4);
    transition: all 0.3s ease;
  }

  .premium-input:focus {
    border-color: hsla(270, 80%, 65%, 0.6);
    box-shadow: 
      0 0 0 3px hsla(270, 80%, 65%, 0.1),
      0 0 20px hsla(270, 80%, 65%, 0.2);
    background: linear-gradient(135deg, 
      hsla(260, 15%, 15%, 0.9) 0%, 
      hsla(270, 12%, 12%, 0.95) 100%);
  }
}

@layer utilities {
  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .animate-pulse-neon {
    animation: pulse-neon 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }
}

@keyframes glow {
  0% {
    box-shadow: 
      0 0 5px hsl(270, 100%, 70%), 
      0 0 10px hsl(270, 100%, 70%), 
      0 0 15px hsl(270, 100%, 70%);
  }
  100% {
    box-shadow: 
      0 0 15px hsl(270, 100%, 70%), 
      0 0 30px hsl(270, 100%, 70%), 
      0 0 45px hsl(270, 100%, 70%);
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes pulse-neon {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

/* Premium Table Styles */
.premium-table {
  background: linear-gradient(135deg,
    hsla(260, 25%, 6%, 0.98) 0%,
    hsla(270, 30%, 8%, 0.95) 25%,
    hsla(280, 25%, 6%, 0.98) 50%,
    hsla(270, 30%, 8%, 0.95) 75%,
    hsla(260, 25%, 6%, 0.98) 100%
  );
  border: 1px solid hsla(270, 50%, 40%, 0.4);
  border-radius: 1rem;
  backdrop-filter: blur(25px);
  box-shadow: 
    0 10px 35px hsla(270, 40%, 5%, 0.6),
    0 0 0 1px hsla(270, 60%, 60%, 0.15),
    inset 0 1px 0 hsla(270, 70%, 80%, 0.1);
  overflow: hidden;
  animation: table-glow 6s ease-in-out infinite;
}

.premium-table-header {
  background: linear-gradient(135deg,
    hsla(270, 40%, 12%, 0.9) 0%,
    hsla(280, 35%, 15%, 0.85) 50%,
    hsla(270, 40%, 12%, 0.9) 100%
  );
  border-bottom: 1px solid hsla(270, 60%, 50%, 0.3);
  backdrop-filter: blur(15px);
}

.premium-table-row {
  background: linear-gradient(135deg, 
    hsla(270, 20%, 8%, 0.6) 0%,
    hsla(280, 15%, 10%, 0.7) 100%);
  border-bottom: 1px solid hsla(270, 40%, 25%, 0.2);
  transition: all 0.3s cubic-bezier(0.23, 1, 0.32, 1);
  position: relative;
}

.premium-table-row::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 3px;
  background: linear-gradient(180deg,
    hsla(270, 100%, 70%, 0) 0%,
    hsla(270, 100%, 70%, 0.8) 50%,
    hsla(270, 100%, 70%, 0) 100%
  );
  transform: scaleY(0);
  transition: transform 0.3s ease;
}

.premium-table-row:hover {
  animation: row-hover-glow 0.3s ease forwards;
  transform: translateX(2px);
}

.premium-table-row:hover::before {
  transform: scaleY(1);
}

.premium-table-cell {
  padding: 1rem 1.5rem;
  color: hsla(270, 15%, 90%, 0.9);
  font-weight: 500;
  transition: color 0.3s ease;
}

.premium-table-row:hover .premium-table-cell {
  color: hsla(270, 20%, 95%, 1);
}

/* Premium Landing Page Styles */
.hero-gradient {
  background: linear-gradient(135deg,
    hsla(260, 60%, 5%, 1) 0%,
    hsla(270, 70%, 8%, 0.95) 20%,
    hsla(280, 50%, 12%, 0.9) 40%,
    hsla(290, 60%, 8%, 0.95) 60%,
    hsla(300, 50%, 10%, 0.9) 80%,
    hsla(260, 60%, 5%, 1) 100%
  );
  position: relative;
  overflow: hidden;
}

.hero-gradient::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: radial-gradient(circle at 30% 20%, hsla(270, 100%, 70%, 0.15) 0%, transparent 50%),
              radial-gradient(circle at 70% 80%, hsla(300, 100%, 70%, 0.1) 0%, transparent 50%),
              radial-gradient(circle at 20% 80%, hsla(240, 100%, 70%, 0.08) 0%, transparent 50%);
  animation: hero-pulse 8s ease-in-out infinite;
}

@keyframes hero-pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.7; }
}

.feature-card-premium {
  background: linear-gradient(135deg,
    hsla(270, 25%, 8%, 0.95) 0%,
    hsla(280, 30%, 10%, 0.9) 50%,
    hsla(270, 25%, 8%, 0.95) 100%
  );
  border: 1px solid hsla(270, 50%, 40%, 0.3);
  backdrop-filter: blur(20px);
  box-shadow: 
    0 8px 32px hsla(270, 50%, 5%, 0.5),
    0 0 0 1px hsla(270, 60%, 60%, 0.1),
    inset 0 1px 0 hsla(270, 70%, 80%, 0.05);
  transition: all 0.4s cubic-bezier(0.23, 1, 0.32, 1);
}

.feature-card-premium:hover {
  transform: translateY(-8px) scale(1.02);
  border-color: hsla(270, 60%, 50%, 0.6);
  box-shadow: 
    0 20px 40px hsla(270, 50%, 5%, 0.7),
    0 0 0 1px hsla(270, 70%, 70%, 0.3),
    inset 0 1px 0 hsla(270, 80%, 90%, 0.1);
}

.premium-input {
  background: linear-gradient(135deg,
    hsla(270, 20%, 10%, 0.8) 0%,
    hsla(280, 25%, 12%, 0.9) 100%
  );
  border: 1px solid hsla(270, 40%, 30%, 0.4);
  color: hsla(270, 15%, 90%, 0.95);
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;
}

.premium-input:focus {
  border-color: hsla(270, 60%, 50%, 0.8);
  box-shadow: 
    0 0 0 3px hsla(270, 60%, 50%, 0.1),
    0 0 20px hsla(270, 70%, 60%, 0.2);
  background: linear-gradient(135deg,
    hsla(270, 25%, 12%, 0.9) 0%,
    hsla(280, 30%, 15%, 0.95) 100%
  );
}

.qr-payment-modal {
  background: linear-gradient(135deg,
    hsla(270, 30%, 6%, 0.98) 0%,
    hsla(280, 35%, 8%, 0.95) 50%,
    hsla(270, 30%, 6%, 0.98) 100%
  );
  border: 1px solid hsla(270, 50%, 40%, 0.4);
  backdrop-filter: blur(30px);
  box-shadow: 
    0 25px 50px hsla(270, 40%, 5%, 0.8),
    0 0 0 1px hsla(270, 60%, 60%, 0.15);
}

@keyframes ambient-flow {
  0%, 100% {
    transform: translateX(0) translateY(0) scale(1);
    opacity: 1;
  }
  33% {
    transform: translateX(-20px) translateY(-10px) scale(1.05);
    opacity: 0.8;
  }
  66% {
    transform: translateX(15px) translateY(10px) scale(0.95);
    opacity: 0.9;
  }
}

@keyframes table-glow {
  0%, 100% {
    box-shadow: 
      0 0 20px hsla(270, 80%, 60%, 0.2),
      0 0 40px hsla(270, 80%, 60%, 0.1),
      inset 0 1px 0 hsla(270, 60%, 80%, 0.1);
  }
  50% {
    box-shadow: 
      0 0 30px hsla(280, 90%, 65%, 0.3),
      0 0 60px hsla(280, 90%, 65%, 0.15),
      inset 0 1px 0 hsla(280, 70%, 85%, 0.15);
  }
}

@keyframes row-hover-glow {
  0% {
    background: linear-gradient(135deg, 
      hsla(270, 30%, 15%, 0.6) 0%,
      hsla(280, 25%, 12%, 0.7) 100%);
    box-shadow: none;
  }
  100% {
    background: linear-gradient(135deg, 
      hsla(270, 50%, 18%, 0.8) 0%,
      hsla(280, 45%, 15%, 0.9) 100%);
    box-shadow: 
      0 4px 20px hsla(270, 70%, 60%, 0.2),
      inset 0 1px 0 hsla(270, 60%, 70%, 0.1);
  }
}

/* Modern Landing Page Animations */
@keyframes gradient {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

.animate-gradient {
  background-size: 200% auto;
  animation: gradient 3s ease infinite;
}

.bg-gradient-radial {
  background-image: radial-gradient(var(--tw-gradient-stops));
}

/* Smooth Scroll */
html {
  scroll-behavior: smooth;
}

/* Compact Feature Cards */
.compact-feature-card {
  background: linear-gradient(135deg, rgba(15, 23, 42, 0.8) 0%, rgba(30, 41, 59, 0.6) 100%);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(147, 51, 234, 0.15);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  min-height: 180px;
}

.compact-feature-card:hover {
  border-color: rgba(147, 51, 234, 0.4);
  transform: translateY(-2px) scale(1.02);
  box-shadow: 0 20px 40px rgba(147, 51, 234, 0.1);
}

/* Compact Stat Cards */
.compact-stat-card {
  background: linear-gradient(135deg, rgba(15, 23, 42, 0.7) 0%, rgba(30, 41, 59, 0.5) 100%);
  backdrop-filter: blur(16px);
  border: 1px solid rgba(148, 163, 184, 0.1);
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
  min-height: 160px;
}

.compact-stat-card:hover {
  border-color: rgba(147, 51, 234, 0.3);
