import React, { Component, ErrorInfo, ReactNode } from 'react';
import { autoAuthCleanup } from '@/lib/auto-auth-cleanup';

interface Props {
  children: ReactNode;
}

interface State {
  hasError: boolean;
  error?: Error;
  errorInfo?: ErrorInfo;
}

/**
 * ERROR BOUNDARY COMPONENT
 * T·ª± ƒë·ªông handle v√† recover t·ª´ React errors
 */
export class ErrorBoundary extends Component<Props, State> {
  public state: State = {
    hasError: false
  };

  public static getDerivedStateFromError(error: Error): State {
    // Update state ƒë·ªÉ hi·ªÉn th·ªã fallback UI
    return { hasError: true, error };
  }

  public componentDidCatch(error: Error, errorInfo: ErrorInfo) {
    console.error('üö® React Error Boundary caught an error:', error, errorInfo);

    this.setState({
      error,
      errorInfo
    });

    // Auto-cleanup n·∫øu error li√™n quan ƒë·∫øn auth
    if (this.isAuthError(error)) {
      console.log('üîß Auth error detected, triggering cleanup...');
      autoAuthCleanup.clearAllAuthData();
      setTimeout(() => {
        window.location.reload();
      }, 1000);
    }

    // Auto-recovery cho c√°c errors kh√°c
    if (this.isRecoverableError(error)) {
      console.log('üîÑ Recoverable error detected, attempting auto-recovery...');
      setTimeout(() => {
        this.setState({ hasError: false, error: undefined, errorInfo: undefined });
      }, 2000);
    }
  }

  private isAuthError(error: Error): boolean {
    return error.message.includes('auth') ||
           error.message.includes('token') ||
           error.message.includes('login') ||
           error.message.includes('Authentication');
  }

  private isRecoverableError(error: Error): boolean {
    return error.message.includes('Network') ||
           error.message.includes('fetch') ||
           error.message.includes('loading');
  }

  private handleRetry = () => {
    console.log('üîÑ User triggered error recovery...');
    this.setState({ hasError: false, error: undefined, errorInfo: undefined });
  };

  private handleReload = () => {
    console.log('üîÑ Full page reload triggered...');
    window.location.reload();
  };

  private handleClearStorage = () => {
    console.log('üßπ Clear storage triggered...');
    autoAuthCleanup.clearAllAuthData();
    window.location.reload();
  };

  public render() {
    // DISABLED: Error boundary card removed to prevent unwanted dialogs in production
    // This fixes the production issue where error card appears during calls
    if (this.state.hasError) {
      console.log('üîß Error boundary triggered but card display disabled:', this.state.error);
      // Silently recover - just render children normally without error card
      return this.props.children;
    }

    return this.props.children;
  }
}

export default ErrorBoundary;

