      toast({
        title: 'Th√†nh c√¥ng',
        description: 'Th√¥ng tin t√†i kho·∫£n ƒë√£ ƒë∆∞·ª£c c·∫≠p nh·∫≠t',
      });
      queryClient.invalidateQueries({ queryKey: ['/api/auth/me'] });
    },
    onError: (error: Error) => {
      toast({
        title: 'L·ªói',
        description: error.message || 'Kh√¥ng th·ªÉ c·∫≠p nh·∫≠t th√¥ng tin',
        variant: 'destructive',
      });
