import { useState } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/api";
import { Loader2, RefreshCw, Phone, CheckCircle, XCircle, Clock } from "lucide-react";

interface TwilioAccount {
  id: number;
  sid: string;
  friendlyName: string;
  isActive: boolean;
  didCount: number;
  lastSyncedAt: string | null;
}

interface DID {
  id: number;
  phoneNumber: string;
  twilioAccountId: number;
  isActive: boolean;
  lastUsed: string | null;
  usageCount: number;
  currentTargetNumber: string | null;
  blockedUntil: string | null;
}

export default function TwilioDidManager() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [syncingAccountId, setSyncingAccountId] = useState<number | null>(null);

  // Fetch Twilio accounts
  const { data: twilioAccounts = [], isLoading: accountsLoading } = useQuery<TwilioAccount[]>({
    queryKey: ['/api/admin/twilio-accounts'],
  });

  // Fetch DIDs
  const { data: dids = [], isLoading: didsLoading } = useQuery<DID[]>({
    queryKey: ['/api/admin/dids'],
  });

  // Sync DIDs mutation
  const syncDidsMutation = useMutation({
    mutationFn: async (accountId: number) => {
      return await apiRequest("POST", `/api/admin/twilio-accounts/${accountId}/sync-dids`);
    },
    onMutate: (accountId) => {
      setSyncingAccountId(accountId);
    },
    onSuccess: (data: any) => {
      toast({
        title: "Sync th√†nh c√¥ng",
        description: `ƒê√£ sync ${data.newDids || 0} s·ªë m·ªõi, c·∫≠p nh·∫≠t ${data.updatedDids || 0} s·ªë hi·ªán c√≥`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/twilio-accounts'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/dids'] });
    },
    onError: (error: any) => {
      toast({
        title: "L·ªói sync DIDs",
        description: error.message || "Kh√¥ng th·ªÉ sync s·ªë t·ª´ Twilio",
        variant: "destructive",
      });
    },
    onSettled: () => {
      setSyncingAccountId(null);
    },
  });

  // Sync all accounts
  const syncAllMutation = useMutation({
    mutationFn: async () => {
      return await apiRequest("POST", "/api/admin/sync-all-dids");
    },
    onSuccess: (data: any) => {
      toast({
        title: "Sync t·∫•t c·∫£ th√†nh c√¥ng",
        description: `T·ªïng c·ªông: ${data.totalNewDids || 0} s·ªë m·ªõi, ${data.totalUpdatedDids || 0} s·ªë c·∫≠p nh·∫≠t`,
      });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/twilio-accounts'] });
      queryClient.invalidateQueries({ queryKey: ['/api/admin/dids'] });
    },
    onError: (error: any) => {
      toast({
        title: "L·ªói sync t·∫•t c·∫£",
        description: error.message || "Kh√¥ng th·ªÉ sync t·ª´ t·∫•t c·∫£ t√†i kho·∫£n Twilio",
        variant: "destructive",
      });
    },
  });

  const formatDate = (dateString: string | null) => {
    if (!dateString) return "Ch∆∞a bao gi·ªù";
    return new Date(dateString).toLocaleString('vi-VN');
  };

  const getDidStatus = (did: DID) => {
    if (!did.isActive) return { label: "T·∫Øt", color: "bg-gray-500" };
    if (did.blockedUntil && new Date(did.blockedUntil) > new Date()) {
      return { label: "ƒêang b·∫≠n", color: "bg-red-500" };
    }
