                >
                  <div className="flex-1">
                    <div className="flex items-center space-x-3">
                      <h3 className="font-semibold text-white">{account.friendlyName}</h3>
                      <Badge className={account.isActive ? "bg-green-600" : "bg-gray-600"}>
