import React from 'react';
import { motion } from 'framer-motion';
import Navigation from './navigation';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface MainLayoutProps {
  children: React.ReactNode;
  user: any;
}

const MainLayout: React.FC<MainLayoutProps> = ({ children, user }) => {
  const { toast } = useToast();

  const handleLogout = async () => {
    try {
      await apiRequest('POST', '/api/auth/logout');

      // Clear any stored tokens
      localStorage.removeItem('token');
      sessionStorage.removeItem('token');

      toast({
        title: "ƒêƒÉng xu·∫•t th√†nh c√¥ng",
        description: "B·∫°n ƒë√£ ƒëƒÉng xu·∫•t kh·ªèi h·ªá th·ªëng",
      });

      // Reload to reset application state
      window.location.reload();
    } catch (error) {
      console.error('Logout error:', error);
      // Force reload even if logout request fails
      window.location.reload();
    }
  };

  return (
    <div className="min-h-screen pro-bg">
      {/* Navigation Header */}
      <Navigation user={user} onLogout={handleLogout} />

      {/* Main Content Area */}
      <motion.main
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.3 }}
        className="relative"
      >
        {/* Content Container */}
