  if (user.role === 'admin') {
    navigationItems.push(
      { path: '/admin', icon: Settings, label: 'Qu·∫£n Tr·ªã', color: 'text-red-400' }
    );
  }

  const isActive = (path: string) => {
    if (path === '/') return location === '/';
    return location.startsWith(path);
  };

